import React, { useState } from 'react';
import './Faq.css';

const faqs = [
  { question: '–ö–∞–∫ —É–∑–Ω–∞—Ç—å –∫—É—Ä—Å –≤–∞–ª—é—Ç?', answer: '–í—ã –º–æ–∂–µ—Ç–µ —É–∑–Ω–∞—Ç—å —Ç–µ–∫—É—â–∏–π –∫—É—Ä—Å –Ω–∞ –Ω–∞—à–µ–º —Å–∞–π—Ç–µ –≤ —Ä–∞–∑–¥–µ–ª–µ "–ö—É—Ä—Å—ã –≤–∞–ª—é—Ç" –∏–ª–∏ –∑–∞–¥–∞—Ç—å –≤–æ–ø—Ä–æ—Å –±–æ—Ç—É.' },
  { question: '–ö–∞–∫ –æ—Ñ–æ—Ä–º–∏—Ç—å –≤–∫–ª–∞–¥ –æ–Ω–ª–∞–π–Ω?', answer: '–ü–µ—Ä–µ–π–¥–∏—Ç–µ –≤ –º–æ–±–∏–ª—å–Ω–æ–µ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ –∏–ª–∏ –Ω–∞ —Å–∞–π—Ç –≤ —Ä–∞–∑–¥–µ–ª "–í–∫–ª–∞–¥—ã" –∏ —Å–ª–µ–¥—É–π—Ç–µ –∏–Ω—Å—Ç—Ä—É–∫—Ü–∏—è–º.' },
  { question: '–ö–∞–∫ —Ä–∞–±–æ—Ç–∞–µ—Ç –æ–±–º–µ–Ω –≤–∞–ª—é—Ç?', answer: '–û–±–º–µ–Ω –æ—Å—É—â–µ—Å—Ç–≤–ª—è–µ—Ç—Å—è —á–µ—Ä–µ–∑ –Ω–∞—à –æ–Ω–ª–∞–π–Ω-—Å–µ—Ä–≤–∏—Å: –≤—ã–±–µ—Ä–∏—Ç–µ –≤–∞–ª—é—Ç—É, –≤–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –∏ –ø–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é.' },
  { question: '–ö–∞–∫ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –¥–æ—Å—Ç—É–ø –∫ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç-–±–∞–Ω–∫—É?', answer: '–ù–∞–∂–º–∏—Ç–µ "–ó–∞–±—ã–ª–∏ –ø–∞—Ä–æ–ª—å?" –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ –≤—Ö–æ–¥–∞ –∏ —Å–ª–µ–¥—É–π—Ç–µ –∏–Ω—Å—Ç—Ä—É–∫—Ü–∏—è–º –¥–ª—è –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è.' },
  { question: '–ì–¥–µ –Ω–∞–π—Ç–∏ –±–ª–∏–∂–∞–π—à–µ–µ –æ—Ç–¥–µ–ª–µ–Ω–∏–µ?', answer: '–ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–∞—Ä—Ç—É –Ω–∞ —Å–∞–π—Ç–µ –∏–ª–∏ –≤ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–∏ ‚Äî –æ–Ω–∞ –ø–æ–∫–∞–∂–µ—Ç –±–ª–∏–∂–∞–π—à–∏–µ –æ—Ç–¥–µ–ª–µ–Ω–∏—è –∏ –±–∞–Ω–∫–æ–º–∞—Ç—ã.' },
  { question: '–ö–∞–∫ –ø–æ–ø–æ–ª–Ω–∏—Ç—å —Å—á—ë—Ç –±–µ–∑ –∫–æ–º–∏—Å—Å–∏–∏?', answer: '–í—ã –º–æ–∂–µ—Ç–µ –ø–æ–ø–æ–ª–Ω–∏—Ç—å —Å—á—ë—Ç —á–µ—Ä–µ–∑ –Ω–∞—à –±–∞–Ω–∫–æ–º–∞—Ç, —Ç–µ—Ä–º–∏–Ω–∞–ª –∏–ª–∏ –ø–µ—Ä–µ–≤–æ–¥–æ–º —Å –¥—Ä—É–≥–æ–≥–æ —Å—á—ë—Ç–∞ –≤ –Ω–∞—à–µ–º –±–∞–Ω–∫–µ.' },
  { question: '–ú–æ–∂–Ω–æ –ª–∏ –æ—Ç–∫—Ä—ã—Ç—å —Å—á—ë—Ç –≤ –∏–Ω–æ—Å—Ç—Ä–∞–Ω–Ω–æ–π –≤–∞–ª—é—Ç–µ?', answer: '–î–∞, –≤—ã –º–æ–∂–µ—Ç–µ –æ—Ç–∫—Ä—ã—Ç—å —Å—á—ë—Ç –≤ –¥–æ–ª–ª–∞—Ä–∞—Ö, –µ–≤—Ä–æ –∏ –¥—Ä—É–≥–∏—Ö –≤–∞–ª—é—Ç–∞—Ö —á–µ—Ä–µ–∑ —Å–∞–π—Ç –∏–ª–∏ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ.' },
  { question: '–ö–∞–∫ —Å–≤—è–∑–∞—Ç—å—Å—è —Å –ø–æ–¥–¥–µ—Ä–∂–∫–æ–π?', answer: '–ù–∞–ø–∏—à–∏—Ç–µ –≤ —á–∞—Ç –ø–æ–¥–¥–µ—Ä–∂–∫–∏ –Ω–∞ —Å–∞–π—Ç–µ –∏–ª–∏ –ø–æ–∑–≤–æ–Ω–∏—Ç–µ –ø–æ –Ω–æ–º–µ—Ä—É –≥–æ—Ä—è—á–µ–π –ª–∏–Ω–∏–∏, —É–∫–∞–∑–∞–Ω–Ω–æ–º—É –≤ –∫–æ–Ω—Ç–∞–∫—Ç–∞—Ö.' },
  { question: '–ß—Ç–æ –¥–µ–ª–∞—Ç—å –ø—Ä–∏ —É—Ç–µ—Ä–µ –∫–∞—Ä—Ç—ã?', answer: '–°—Ä–∞–∑—É –∑–∞–±–ª–æ–∫–∏—Ä—É–π—Ç–µ –∫–∞—Ä—Ç—É —á–µ—Ä–µ–∑ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ –∏–ª–∏ –ø–æ–∑–≤–æ–Ω–∏—Ç–µ –≤ –ø–æ–¥–¥–µ—Ä–∂–∫—É. –ó–∞—Ç–µ–º –∑–∞–∫–∞–∂–∏—Ç–µ –ø–µ—Ä–µ–≤—ã–ø—É—Å–∫.' },
];


const FAQ = () => {
  const [openIndex, setOpenIndex] = useState(null);

  const toggle = (idx) => {
    setOpenIndex(openIndex === idx ? null : idx);
  };

  return (
    <div className="faq">
      <h3>üè∑ –ß–∞—Å—Ç–æ –∑–∞–¥–∞–≤–∞–µ–º—ã–µ –≤–æ–ø—Ä–æ—Å—ã</h3>
      <ul className="faq-list">
        {faqs.map((item, idx) => (
          <li key={idx} className="faq-item">
            <div className="faq-question" onClick={() => toggle(idx)}>
              {item.question}
            </div>
            {openIndex === idx && (
              <div className="faq-answer">{item.answer}</div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};


export default FAQ;
