import React, {useReducer, useEffect} from "react";
import axios from "axios";
import Buttoni from "../Buttoni/Buttoni";
import Smska from "../Smska/Smska";
import Buttoniany from "../Buttoniany/Buttoniany";
import Export from "../Export/Export";
import ButtonsForAccept from "../ButtonsForAccept/ButtonsForAccept";
import TradeCurrency from "../TradeCurrency/TradeCurrency";
import './Dialog.css'
import Deposit from "../Deposit/Deposit";
import Deposit2 from "../Deposit/Nakopschet";
import EqualButtons from "../EqualButtons";

// Initial state
const initialState = {
  valutate: '',
  money: false,
  counts: false,
  message: '',
  banker: '',
  messages: [],
  input: "",
  country: '',
  point: {
    Balance: 0,
    Plus: 0,
    Money: 0,
    Kopit: 0,
    Big: 0,
    Vozmozh: 0,
  },
  pointnames: {
    Balance: 'В балансе',
    Plus: 'В плюсе',
    Money: 'Новые деньги',
    Kopit: 'Копить',
    Big: 'Большая Выгода',
    Vozmozh: 'Расширяй возможности',
  }
};

// Reducer function
function dialogReducer(state, action) {
  switch (action.type) {
    case 'SET_VALUTATE':
      return {...state, valutate: action.payload};
    case 'SET_MONEY':
      return {...state, money: action.payload};
    case 'SET_COUNTS':
      return {...state, counts: action.payload};
    case 'SET_MESSAGE':
      return {...state, message: action.payload};
    case 'SET_BANK':
      return {...state, banker: action.payload};
    case 'SET_MESSAGES':
      return {...state, messages: action.payload};
    case 'ADD_MESSAGE':
      return {...state, messages: [...state.messages, action.payload]};
    case 'SET_INPUT':
      return {...state, input: action.payload};
    case 'SET_COUNTRY':
      return {...state, country: action.payload};
    case 'UPDATE_POINT':
      return {
        ...state,
        point: {
          ...state.point,
          [action.payload.key]: state.point[action.payload.key] + action.payload.value
        }
      };
    case 'RESET_POINT':
      return {
        ...state,
        point: {
          Balance: 0,
          Plus: 0,
          Money: 0,
          Kopit: 0,
          Big: 0,
          Vozmozh: 0,
        }
      };
    case 'CLEAR_MESSAGES':
      return {
        ...state,
        messages: [
          {
            sender: 'bot-message',
            component: (
              <Buttoniany
                first="👋 Пока, если нужна будет помощь, я тут"
                buttons={['💸 Комиссия', '💱 Обмен валюты', '🏦 Вклады и счета']}
                onClickHandler={[comissia, trade_valuta, vkladiandscheta]}
              />
            )
          }
        ]
      };
    default:
      return state;
  }
}

export default function Dialog({yourmessage}) {
  const [state, dispatch] = useReducer(dialogReducer, initialState);
  const {valutate, money, counts, message, banker, messages, input, country, point, pointnames} = state;

  // Action creators
  const setValutate = (value) => dispatch({type: 'SET_VALUTATE', payload: value});
  const setMoney = (value) => dispatch({type: 'SET_MONEY', payload: value});
  const setCounts = (value) => dispatch({type: 'SET_COUNTS', payload: value});
  const setMessage = (value) => dispatch({type: 'SET_MESSAGE', payload: value});
  const setBank = (value) => dispatch({type: 'SET_BANK', payload: value});
  const setMessages = (value) => dispatch({type: 'SET_MESSAGES', payload: value});
  const addMessage = (value) => dispatch({type: 'ADD_MESSAGE', payload: value});
  const setInput = (value) => dispatch({type: 'SET_INPUT', payload: value});
  const setCountry = (value) => dispatch({type: 'SET_COUNTRY', payload: value});
  const updatePoint = (key, value) => dispatch({type: 'UPDATE_POINT', payload: {key, value}});
  const resetPoint = () => dispatch({type: 'RESET_POINT'});
  const clearMessages = () => dispatch({type: 'CLEAR_MESSAGES'});

  useEffect(() => {
    setMessages([
      {sender: 'user-message', text: yourmessage},
      {
        sender: 'bot-message',
        component: (
          <Buttoniany
            first="👋 Привет, с чем конкретно тебе помочь?"
            buttons={['💸 Комиссия', '💱 Обмен валюты', '🏦 Вклады и счета']}
            onClickHandler={[comissia, trade_valuta, vkladiandscheta]}
          />
        ),
      },
    ]);
  }, [yourmessage]);

  const comissia = () => {
    addMessage({sender: 'user-message', text: 'Рассчитай комиссию'});
    addMessage({
      sender: 'bot-message',
      component: (
        <>
          <p>💱 Выбери валюту</p>
          <Export func={countries} choose={2}/>
        </>
      ),
    });
  };

  const countries = (valu) => {
    setValutate(valu);
    addMessage({sender: 'user-message', text: valu});
    addMessage({
      sender: 'bot-message', component: (
        <>
          <p>🌏 Выбери страну в которую хочешь сделать перевод</p>
          <Export func={valuta} choose={1}/>
        </>
      )
    });
  };

  const vkladiandscheta = (info) => {
    addMessage({sender: 'user-message', text: info});
    addMessage({
      sender: 'bot-message', component: (
        <Buttoniany
          first={'🧐 Что тебя интересует '}
          buttons={['💎 Накопительный счет', '📠 Вклады']}
          onClickHandler={[nakopschet, vklad]}
        />
      )
    });
  };

  const nakopschet = (text) => {
    addMessage({sender: 'user-message', text: text});
    addMessage({
      sender: 'bot-message', component: (
        <>
          <div className="promo-banner">
            <h2>💼 Почему накопительный счет?</h2>
            <p>
              Накопительный счёт — надёжный инструмент для хранения и приумножения средств за счёт
              стабильных процентов. Помогает сформировать «подушку безопасности», достичь финансовых
              целей и спокойно планировать будущее. 🌙💸
            </p>
          </div>

          <Buttoniany
            first={''}
            buttons={['🧩 Преимущества', '🎯 Помоги выбрать', '✨ Категории и счета', '🔙 Назад']}
            onClickHandler={[nakopschetinfo, nakopshetchoose, nakopshetcategor, vkladiandscheta]}
          />
        </>
      )
    });
  };

  const nakopshetcategor = (info) => {
    addMessage({sender: 'user-message', text: info});
    addMessage({
      sender: 'bot-message', component: (
        <>
          <p>Все категории в нашем банке вы можете увидеть по ссылке 👉 </p>
          <a href="https://www.gazprombank.ru/personal/increase/deposits/">https://www.gazprombank.ru/personal/increase/deposits/</a>
        </>
      )
    });
  };

  const nakopshetchoose = (info) => {
    addMessage({sender: 'user-message', text: info});
    addMessage({
      sender: 'bot-message', component: (
        <Buttoni first={'👀 Сколько денег вы хотите держать на счёте?'}
                 buttons={['😎 До 10 млн', '🤑 Свыше 10 млн']}
                 onClickHandler={nakopshetchoosestep2}
        />
      )
    });
  };

  const nakopshetchoosestep2 = (choose) => {
    setMoney(choose === '😎 До 10 млн');
    addMessage({sender: 'user-message', text: choose});
    addMessage({
      sender: 'bot-message', component: (
        <Buttoni
          first={'👾 Будете ли вы часто снимать или пополнять счет?'}
          buttons={['✔️ Да', '❌ Нет']}
          onClickHandler={nakopshetchoosestep3}
        />
      )
    });
  };

  const nakopshetchoosestep3 = (info) => {
    setCounts(info === '✔️ Да');
    addMessage({sender: 'user-message', text: info});
    addMessage({
      sender: 'bot-message', component: (
        <Buttoni first={'Что для вас важнее?'}
                 buttons={['Максимальная ставка (до 21,5 % годовых)', 'Простые условия (19,5 % без ограничения операций)']}
                 onClickHandler={nakopshetchoosestep4}
        />
      )
    });
  };

  const nakopshetchoosestep4 = (info) => {
    const sms = info === 'Максимальная ставка (до 21,5 % годовых)' ? 'Максимальная' : 'Простые условия';
    addMessage({sender: 'user-message', text: sms});

    let result = '';
    if (money && !counts && info === 'Максимальная ставка (до 21,5 % годовых)') {
      result = 'Premium';
    } else if (!money && !counts && info === 'Максимальная ставка (до 21,5 % годовых)') {
      result = 'Накопительный счет';
    } else if (counts && info === 'Максимальная ставка (до 21,5 % годовых)') {
      result = 'Ежедневный процент';
    } else if (info === 'Простые условия (19,5 % без ограничения операций)') {
      result = 'Простой процент';
    }

    if (result) {
      addMessage({sender: 'bot-message', text: result});
    }

    addMessage({
      sender: 'bot-message', component: (
        <Buttoniany
          first="Может быть тебе помочь с чем то другим?"
          buttons={['Комиссия', 'Обмен валюты', 'Вклады и счета']}
          onClickHandler={[comissia, trade_valuta, vkladiandscheta]}
        />
      )
    });
  };

  const nakopschetinfo = (info) => {
    addMessage({sender: 'user-message', text: info});
    addMessage({
      sender: 'bot-message', component: <Deposit2/>
    });
  };

  const vklad = (text) => {
    addMessage({sender: 'user-message', text: text});
    addMessage({
      sender: 'bot-message', component: (
        <>
          <div className="promo-banner">
            <h2>💼 Почему стоит открыть вклад?</h2>
            <p>
              <strong>Вклады</strong> помогают не только <strong>сохранить ваши сбережения в безопасности</strong>,
              но и <strong>приумножить</strong> их за счёт <strong>стабильных процентных начислений</strong>.<br/>
              Это <strong>надёжный способ планировать будущее</strong> и <strong>заставить деньги работать на вас</strong> — даже во сне! 🌙💸
            </p>
          </div>
          <Buttoniany
            first={''}
            buttons={['Помочь выбрать вклад', 'Категории', 'Преимущества']}
            onClickHandler={[helpchoosevklad, categories, privileges]}
          />
        </>
      )
    });
  };

  const helpchoosevklad = (text) => {
    resetPoint();
    addMessage({sender: 'user-message', text: text});
    addMessage({
      sender: 'bot-message', component: (
        <EqualButtons
          first={'👍 Выбери валюту в которой будешь держать вклад'}
          buttons={['Рубли', 'Юани']}
          onClickHandler={currency}
          value={['Рубли', 'Юани']}
        />
      )
    });
  };

  const currency = (voted) => {
    addMessage({sender: 'user-message', text: voted});

    if (voted === 'Рубли') {
      updatePoint('Balance', 1);
      updatePoint('Plus', 1);
      updatePoint('Money', 1);
      updatePoint('Kopit', 1);
      updatePoint('Big', 1);
    } else {
      updatePoint('Vozmozh', 1);
    }

    addMessage({
      sender: 'bot-message', component: (
        <Buttoni
          first={'😊 На какой срок вы планируете разместить средства?'}
          buttons={['1 - 3 мес', '4 - 7 мес', '8 мес - 3 года']}
          onClickHandler={times}
        />
      )
    });
  };

  const times = (times) => {
    addMessage({sender: 'user-message', text: times});

    if (times === '1 - 3 мес' || times === '4 - 7 мес') {
      updatePoint('Balance', 1);
      updatePoint('Money', 1);
      updatePoint('Kopit', 1);
      updatePoint('Plus', 1);
    }

    addMessage({
      sender: 'bot-message', component: (
        <Buttoni
          first={'🙂 Как вам удобнее получать проценты?'}
          buttons={['Ежемесячно на счет / карту', 'В конце срока']}
          onClickHandler={procents}
        />
      )
    });
  };

  const procents = (procents) => {
    addMessage({sender: 'user-message', text: procents});

    if (procents === 'Ежемесячно на счет / карту') {
      updatePoint('Balance', 1);
    } else {
      updatePoint('Plus', 1);
      updatePoint('Money', 1);
      updatePoint('Kopit', 1);
      updatePoint('Big', 1);
    }

    addMessage({
      sender: 'bot-message', component: (
        <>
          <div className="promo-banner">
            <h2>💼 Готовы ли вы выполнить какие-либо условия, чтобы получить надбавку к базовой ставке?</h2>
            <p>
              1)Никаких условий, только максимальная фиксированная ставка <br/>
              2)Поддерживать средний остаток на дебетовой карте <br/>
              3)Подключить опцию «Накопления» в сервисе «Газпром Бонус» <br/>
              4)Внести «новые деньги» (не было 30 дней на ваших счетах) <br/>
              5)Открыть дистанционно и быть новым/зарплатным/пенсионным клиентом! 🌙💸
            </p>
          </div>
          <Buttoni first={''}
                   buttons={['1', '2', '3', '4', '5']}
                   onClickHandler={ysl}
          />
        </>
      )
    });
  };

  const ysl = (vote) => {
    addMessage({sender: 'user-message', text: vote});

    if (vote === '1') {
      updatePoint('Big', 1);
    } else if (vote === '2') {
      updatePoint('Balance', 1);
    } else if (vote === '3') {
      updatePoint('Plus', 1);
    } else if (vote === '4') {
      updatePoint('Money', 1);
    } else {
      updatePoint('Kopit', 1);
    }

    const maxValue = Math.max(...Object.values(point));
    const itog = [];

    for (const key in point) {
      if (point[key] === maxValue) {
        itog.push(key);
      }
    }

    const user_itog = itog.map(key => pointnames[key]);

    addMessage({
      sender: 'bot-message', component: (
        <>
          {user_itog.map((key, itogi) => (
            <p key={itogi}>Тебе подходят такие вклады как: {key}</p>
          ))}
        </>
      )
    });
  };

  const categories = (text) => {
    addMessage({sender: 'user-message', text: text});
    addMessage({
      sender: 'bot-message', component: (
        <>
          <p>Все категории в нашем банке вы можете увидеть по ссылке 👉 </p>
          <a href="https://www.gazprombank.ru/personal/increase/deposits/">https://www.gazprombank.ru/personal/increase/deposits/</a>
        </>
      )
    });
  };

  const privileges = (text) => {
    addMessage({sender: 'user-message', text: text});
    addMessage({
      sender: 'bot-message', component: <Deposit/>
    });
  };

  const trade_valuta = () => {
    addMessage({sender: 'user-message', text: 'Обмен валюты'});
    addMessage({
      sender: 'change-moneta', component: <TradeCurrency/>
    });
    addMessage({
      sender: 'second-bot-message', component: (
        <Buttoniany
          first="👀 Может теперь нужна помощь с чем-то другим?"
          buttons={['💸 Комиссия', '💱 Обмен валюты', '🏦 Вклады и счета']}
          onClickHandler={[comissia, trade_valuta, vkladiandscheta]}
        />
      )
    });
  };

  const valuta = (country) => {
    setCountry(country);
    addMessage({sender: 'user-message', text: country});
    addMessage({sender: 'bot-message', text: '✍️ Напиши сумму которую ты хочешь перевести'});
  };

  const Sendtoback = async (data) => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/", data, {
        headers: {"Content-Type": "application/json"},
      });

      addMessage({
        sender: 'bot-message', component: (
          <>
            <p>Ваша комиссия составит: undefined </p>
          </>
        )
      });

      console.log("Успешный ответ и данные отправлены", response.data);
    } catch (error) {
      console.error("Ошибка при отправке данных:", error);
    }
  };

  const bank = (bankik, summa) => {
    setBank(bankik);
    addMessage({sender: 'user-message', text: bankik});
    addMessage({
      sender: 'bot-message',
      component: (
        <>
          <h3>✅ Информация о переводе</h3>
          <p>💵 Валюта: {valutate}</p>
          <p>🌍 Страна: {country}</p>
          <p>💰 Сумма: {summa}</p>
          <p> 🏦 Банк: {bankik}</p>
          <button className="button" onClick={() => Sendtoback({valutate, summa, bankik, country})}>
            Все верно
          </button>
        </>
      ),
    });
  };

  const bankers = (msg) => {
    addMessage({
      sender: 'bot-message',
      component: (
        <>
          <p>Выбери банк в который хочешь сделать перевод</p>
          <Export func={bank} choose={0} val2={msg}/>
        </>
      ),
    });
  };

  const handleSend = (e) => {
    e.preventDefault();
    const trimmed = input.trim();
    if (!trimmed) return;

    addMessage({sender: 'user-message', text: trimmed});

    if (valutate && !message) {
      const msg = trimmed;
      if (!isNaN(msg) && isFinite(msg) && Number(msg) > 0) {
        if (valutate && !message) {
          setMessage(msg);
          bankers(msg);
        }
      } else {
        addMessage({sender: 'bot-message', text: 'Ты ввел неверное число'});
      }
    } else {
      const lastComponent = [...messages].reverse().find(m => m.component)?.component;

      addMessage({sender: 'bot-message', text: '😩 Прости, пока затрудняюсь ответить на твой вопрос'});

      if (lastComponent) {
        addMessage({sender: 'second-bot-message', component: lastComponent});
      }
    }

    setInput("");
  };

  return (
    <div>
      <Smska
        input={input}
        handleSend={handleSend}
        setInput={setInput}
        messages={messages}
        clear={clearMessages}
      />
    </div>
  );
}
