import React, {useState} from 'react'
import axios from "axios";
import Buttoni from "./Buttoni";
import Smska from "./Smska";

export default function Dialog({yourmessage}) {
    const [flag, setFlag] = useState(false);
    const [flag2, setFlag2] = useState(false);
    const [flag3, setFlag3] = useState(false);
    const [valutate, setValutate] = useState('');
    const [message, setMessage] = useState('');
    const [banker, setBank] = useState('');
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");

    function comissia(text) {
        if (flag) {
            console.log('Уже нажато');
            return;
        }
        setFlag(true);
        setMessages(prev => [
            ...prev,
            {type: 'text', sender: 'user', text}, // пользователь нажал кнопку
            {
                type: 'component',
                sender: 'bot',
                component: <Buttoni
                    first="Выбери валюту"
                    buttons={["Рубли", "Евро", "Доллары"]}
                    onClickHandler={valuta}
                />
            }
        ]);

    }

    function valuta(valida) {
        if (flag2) {
            console.log('Уже нажато');
            return;
        }

        setValutate(valida);
        setFlag2(true);
        setMessages(prev => [
            ...prev,
            {type: 'text', sender: 'user', valutate},
            {type: 'text', sender: 'user', text: 'Напиши сумму которую ты хочешь перевести'},
            {type: 'text', sender: 'user', message},
        ]);

    }

    function bank(bankik) {
        if (flag) {
            console.log('Уже нажато');
            return;
        }
        setBank(bankik)
        setFlag3(true);

    }

    const Sendtoback = async () => {
        const data = {
            valutate, banker, message
        };
        try {
            const response = await axios.post("http://127.0.0.1:8000/", data, {
                headers: {"Content-Type": "application/json"}
            });

            console.log("Успешный ответ и данные отправлены", response.data);
        } catch (error) {
            console.error("Ошибка при отправке данных:", error);
        }

    }
    const handleSend = async (event) => {
        event.preventDefault();
        if (input.trim() === "") return;
        setMessages([...messages, {text: input, sender: "user"}]);
        if (flag2) {
            setMessage(input)
        }
        setInput("");

    };
    return (
        <>
            <div className={'chat-container'}>
                <div className={'user'}>
                    <p>{yourmessage}</p>
                </div>
                <Buttoni
                    first="Привет, с чем конкретно тебе помочь?"
                    buttons={["Комиссия", "Обмен валюты", "Че-то еще"]}
                    onClickHandler={comissia}
                />


                {flag3 ?
                    <>
                        <div className={'user'}>{banker}</div>
                        <div className={'bot'}>
                            <p>Давайте полностью проверим информацию</p>
                            <p>Валюта : {valutate} </p>
                            <p>Сумма : {message}</p>
                            <p>Банк: {banker} </p>
                            <button className={'button'} onClick={Sendtoback}>Все верно</button>
                        </div>
                    </>
                    : null

}

<Smska input={input} handleSend={handleSend} setInput={setInput} messages={messages}/>
</div>
<button onClick={() => window.location.reload()} className={'cleaning'}>Очистить все нах</button>
</>
)
}
