import React, {useState} from 'react'
import Soobchenie from "./Soobchenie";

export default function Dialog({yourmessage}) {
    const [flag, setFlag] = useState(false);
    const [flag2, setFlag2] = useState(false);
    const [valutate, setValutate] = useState('');
    const [message, setMessage] = useState('');
    const [submittedMessage, setSubmittedMessage] = useState('');
    const handleSubmit = async (event) => {
        event.preventDefault();
        setSubmittedMessage(message);
        setMessage('');
    };

    function comissia() {
        if (flag) {
            console.log('Уже нажато');
        } else {
            setFlag(true);
        }
    }
    function valuta (){
            if (flag2) {
                console.log('Uses');
            } else {
                setValutate('рубли');
                setFlag2(true);
            }
    }

    return (
        <>
            <div className={'chat-container'}>
                 <Soobchenie message={message} setMessage={setMessage} handleSubmit={handleSubmit}/>
            <div className={'user'}>
                <p>{yourmessage}</p>
            </div>
            <div className={'bot'}>
                <p>Привет дорогой пользователь ты очень крут <br/> пожалуйста выбери одну из этих кнопочек</p>
                <button onClick={comissia}>Коммисия</button>
                <button>Обмен валюты</button>
                <button>Че то еще</button>
            </div>
            {flag ? <>
                    <div className={'user'}>Рассчитай комиссию</div>
                    <div className={'bot'}>
                        <p>Выбери валюту</p>
                        <button onClick={valuta}>Рубли</button>
                        <button>Евро</button>
                        <button>Доллары</button>
                    </div>

                </>
                : null}
            {flag2? <>
                    <div className={'user'}>Моя вылюта {valutate}</div>
                    <div className={'bot'}>Напиши сумму которую ты хочешь перевести</div>
                    <div className={'user'}>{submittedMessage}</div>
                    {isNan(submittedMessage)}

                </>
                : null}

            </div>



        </>


    )
}
