import React, { useState, useEffect } from "react";
import axios from "axios";
import Buttoni from "./Buttoni";
import Smska from "./Smska";

export default function Dialog({ yourmessage }) {
    const [valutate, setValutate] = useState('');
    const [message, setMessage] = useState('');
    const [banker, setBank] = useState('');
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    // Функции для обработки кликов
    function comissia(text) {
        setMessages((prevMessages) => [
            ...prevMessages,
            { sender: "bot", text: "Рассчитай комиссию" },
            { sender: "bot", component: (
                <Buttoni
                    first="Выбери валюту"
                    buttons={["Рубли", "Евро", "Доллары"]}
                    onClickHandler={valuta}
                />
            )}
        ]);
    }

    function valuta(valida) {
        setValutate(valida);
        setMessages((prevMessages) => [
            ...prevMessages,
            { sender: "bot", text: `Вы выбрали валюту: ${valida}` },
            { sender: "bot", text: "Напиши сумму, которую ты хочешь перевести" }
        ]);
    }

    function bank(bankik) {
        setBank(bankik);
        setMessages((prevMessages) => [
            ...prevMessages,
            { sender: "bot", text: `Вы выбрали банк: ${bankik}` },
            { sender: "bot", text: "Давайте проверим информацию" },
            { sender: "bot", text: `Валюта: ${valutate}, Сумма: ${message}, Банк: ${bankik}` }
        ]);
    }

    const Sendtoback = async () => {
        const data = { valutate, banker, message };
        try {
            const response = await axios.post("http://127.0.0.1:8000/", data, {
                headers: { "Content-Type": "application/json" }
            });
            console.log("Успешный ответ и данные отправлены", response.data);
        } catch (error) {
            console.error("Ошибка при отправке данных:", error);
        }
    }

    const handleSend = async (event) => {
        event.preventDefault();
        if (input.trim() === "") return;

        // Добавляем сообщение от пользователя
        setMessages((prevMessages) => [
            ...prevMessages,
            { sender: "user", text: input }
        ]);

        // Если введена сумма, сохраняем её
        if (valutate) {
            setMessage(input);
        }
        setInput("");
    };

    useEffect(() => {
        // Добавляем начальные сообщения при монтировании компонента
        setMessages((prevMessages) => [
            ...prevMessages,
            { sender: "user", text: yourmessage },
            {
                sender: "bot",
                component: (
                    <Buttoni
                        first="Привет, с чем конкретно тебе помочь?"
                        buttons={["Комиссия", "Обмен валюты", "Че-то еще"]}
                        onClickHandler={comissia}
                    />
                )
            }
        ]);
    }, [yourmessage]);

    return (
        <>
            <div className="chat-container">
                <Smska
                    input={input}
                    handleSend={handleSend}
                    setInput={setInput}
                    messages={messages}
                />
            </div>
            <button onClick={() => window.location.reload()} className="cleaning">
                Очистить все нах
            </button>
        </>
    );
}
