import React, { useState, useEffect } from "react";
import axios from "axios";
import Buttoni from "./Buttoni";
import Smska from "./Smska";

export default function Dialog({ yourmessage }) {
    const [valutate, setValutate] = useState('');
    const [message, setMessage] = useState('');
    const [banker, setBank] = useState('');
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");

    // Инициализация диалога
    useEffect(() => {
        setMessages([
            { sender: 'user', text: yourmessage },
            {
                sender: 'bot',
                component: (
                    <Buttoni
                        first="Привет, с чем конкретно тебе помочь?"
                        buttons={['Комиссия', 'Обмен валюты', 'Че-то еще']}
                        onClickHandler={comissia}
                    />
                ),
            },
        ]);
    }, []);

    // Обработка выбора "Комиссия"
    const comissia = () => {
        setMessages(prev => [
            ...prev,
            { sender: 'user', text: 'Рассчитай комиссию' },
            {
                sender: 'bot',
                component: (
                    <Buttoni
                        first="Выбери валюту"
                        buttons={['Рубли', 'Евро', 'Доллары']}
                        onClickHandler={valuta}
                    />
                ),
            },
        ]);
    };

    // Обработка выбора валюты
    const valuta = (val) => {
        setValutate(val);
        setMessages(prev => [
            ...prev,
            { sender: 'user', text: val },
            { sender: 'bot', text: 'Напиши сумму которую ты хочешь перевести' },
        ]);
    };

    // Обработка ввода суммы пользователем
    const handleSend = (e) => {
        e.preventDefault();
        const trimmed = input.trim();
        if (!trimmed) return;

        setMessages(prev => [...prev, { sender: 'user', text: trimmed }]);

        if (valutate && !message) {
            setMessage(trimmed);

            if (!isNaN(trimmed) && isFinite(trimmed) && Number(trimmed) > 0) {
                setMessages(prev => [
                    ...prev,
                    {
                        sender: 'bot',
                        component: (
                            <Buttoni
                                first="Выбери банк"
                                buttons={['Сбербанк', 'Т-банк', 'Совкомбанк']}
                                onClickHandler={bank}
                            />
                        ),
                    },
                ]);
            } else {
                setMessages(prev => [
                    ...prev,
                    { sender: 'bot', text: 'Ты ввел неверное число' },
                ]);
            }
        }

        setInput("");
    };

    // Обработка выбора банка
    const bank = (bankik) => {
        setBank(bankik);
        setMessages(prev => [
            ...prev,
            { sender: 'user', text: bankik },
            {
                sender: 'bot',
                text:
                    `Давайте полностью проверим информацию:\n` +
                    `Валюта: ${valutate}\n` +
                    `Сумма: ${message}\n` +
                    `Банк: ${bankik}`,
            },
            {
                sender: 'bot',
                component: (
                    <button className="button" onClick={Sendtoback}>
                        Все верно
                    </button>
                ),
            },
        ]);
    };

    // Отправка на backend
    const Sendtoback = async () => {
        try {
            const data = { valutate, banker, message };
            const response = await axios.post("http://127.0.0.1:8000/", data, {
                headers: { "Content-Type": "application/json" },
            });
            console.log("Успешный ответ и данные отправлены", response.data);
        } catch (error) {
            console.error("Ошибка при отправке данных:", error);
        }
    };

    return (
        <>
            <div className="chat-container">
                <div>
                    {messages.map((msg, index) => (
                        <div
                            key={index}
                            className={msg.sender === 'user' ? 'user' : 'bot'}
                        >
                            {msg.text &&
                                msg.text.split('\n').map((line, i) => (
                                    <p key={i}>{line}</p>
                                ))}
                            {msg.component && msg.component}
                        </div>
                    ))}
                </div>
                
            </div>

            <button
                onClick={() => window.location.reload()}
                className="cleaning"
            >
                Очистить все нах
            </button>
        </>
    );
}
